mapper.select:


	SELECT 	A.KBLNUM 	KBLNUM	
	FROM 	KOSMO_BOARD_LIKE A
	WHERE 	A.DELETEYN = 'Y' 
	AND     A.KMNUM = #{kmnum}	
	AND     A.KBNUM = #{kbnum}



	SELECT 	*
	FROM 	KOSMO_BOARD_LIKE A
	WHERE 	A.DELETEYN = 'Y' 
	AND     A.KMNUM = #{kmnum}	
	AND     A.KBNUM = #{kbnum}

mapper.update:
    
	UPDATE 	KOSMO_BOARD_LIKE 
	   SET  KBLIKE_CNT_1 = KBLIKE_CNT_1 + 1
		   ,UPDATEDATE = SYSDATE
	 WHERE  KMNUM = #{kmnum}
	 AND    KBNUM = #{kbnum}

    
	UPDATE 	KOSMO_BOARD_LIKE 
	   SET  KBLIKE_CNT_2 = KBLIKE_CNT_2 + 1
		   ,UPDATEDATE = SYSDATE
	 WHERE  KMNUM = #{kmnum}
	 AND    KBNUM = #{kbnum}

    
	UPDATE 	KOSMO_BOARD_LIKE 
	   SET  KBLIKE_CNT_3 = KBLIKE_CNT_3 + 1
		   ,UPDATEDATE = SYSDATE
	 WHERE  KMNUM = #{kmnum}
	 AND    KBNUM = #{kbnum}

    
	UPDATE 	KOSMO_BOARD_LIKE 
	   SET  KBLIKE_CNT_4 = KBLIKE_CNT_4 + 1
		   ,UPDATEDATE = SYSDATE
	 WHERE  KMNUM = #{kmnum}
	 AND    KBNUM = #{kbnum}

    
	UPDATE 	KOSMO_BOARD_LIKE 
	   SET  KBLIKE_CNT_5 = KBLIKE_CNT_5 + 1
		   ,UPDATEDATE = SYSDATE
	 WHERE  KMNUM = #{kmnum}
	 AND    KBNUM = #{kbnum}

    
	UPDATE 	KOSMO_BOARD_LIKE 
	   SET  KBLIKE_CNT_6 = KBLIKE_CNT_6 + 1
		   ,UPDATEDATE = SYSDATE
	 WHERE  KMNUM = #{kmnum}
	 AND    KBNUM = #{kbnum}
